import { actionTypes } from "./types";

const INITIAL_STATE = {
  currentUser: null,
  isUserSignIn: false,
};

export const userReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case actionTypes.SET_CURRENT_USER:
      return {
        ...state,
        currentUser: action.payload,
      };
      case actionTypes.IS_USER_SIGN_IN:
          return {
              ...state,
              isUserSignIn: action.payload,
          };
    default:
      return state;
  }
};
