import Homepage from "./pages/homepage";
import EndGamePage from "./pages/endGamePage";
import SignInAndSignUpPage from "./pages/sign-in-and-sign-up";
import { auth, createUserProfileDocument } from "./firebase/utils";

import "./App.css";

