import { actionTypes } from "./types";

export const setCurrentUser = (currentUser) => ({
  type: actionTypes.SET_CURRENT_USER,
  payload: currentUser,
});

export const setIsUserSignIn = (isUserSignIn) => ({
    type: actionTypes.IS_USER_SIGN_IN,
    payload: isUserSignIn,
});
